element.calculation:
(function calculatedFieldValue(current) {

	var impactedSerials = 0;
    var impactedSerialGA = new GlideAggregate(DXCObsidianConstants.OBSIDIAN_IMPACTED_SERIALS_TABLE);
    impactedSerialGA.addQuery('technical_ticket.tsm', current.getUniqueValue());
	impactedSerialGA.addAggregate('COUNT');
    impactedSerialGA.query();

    if (impactedSerialGA.next()){
		impactedSerials = impactedSerialGA.getAggregate('COUNT');
	}

    return impactedSerials;

})(current);

(function calculatedFieldValue(current) {

	var relatedTickets = 0;
    var relatedTicketsGA = new GlideAggregate(DXCObsidianConstants.TECHNICAL_TICKET_TABLE);
    relatedTicketsGA.addQuery('tsm', current.getUniqueValue());
	relatedTicketsGA.addAggregate('COUNT');
    relatedTicketsGA.query();

    if (relatedTicketsGA.next()){
		relatedTickets = relatedTicketsGA.getAggregate('COUNT');
	}

    return relatedTickets;

})(current);
