catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (newValue == '') {
        g_form.clearValue("existing_project");
        g_form.clearValue("model_requester_type");
        //  g_form.setDisplay("existing_project", false);
        return;
    }

    var ga = new GlideAjax("x_nesa_ng_obsidian.DXCObsidianNMLUtilsAJAX");
    ga.addParam("sysparm_name", "processModelRequesterGroup");
    ga.addParam("sysparm_group_sys_id", newValue);
    ga.getXML(function (response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        var answerJSON = JSON.parse(answer);
        if (answerJSON.model_requester_type === "sbu" || answerJSON.model_requester_type === "market") {
            g_form.setValue("model_requester_type", answerJSON.model_requester_type);
            if (answerJSON.model_requesting_country_sys_id) {
                g_form.setValue("model_requesting_country", answerJSON.model_requesting_country_sys_id, answerJSON.model_requesting_country_display_value);
            } else {
                g_form.clearValue(("model_requesting_country"));
            }
        } else {
            alert("there was an error during the onChange processing of the model requester group variable");
        }

    });


}
