catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (newValue == '') {
        g_form.clearValue("assignment_country");
        g_form.hideFieldMsg("assignment_country", true);

        return;
    }

    var ga = new GlideAjax("x_nesa_ng_obsidian.DXCObsidianSKUUtilsAJAX");
    ga.addParam("sysparm_name", "processModelRequesterGroup");
    ga.addParam("sysparm_group_sys_id", newValue);
    ga.getXML(function (response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        var answerJSON = JSON.parse(answer);
        if (answerJSON.sku_requester_group_has_countries) {
            g_form.hideFieldMsg("assignment_country", true);
            if (answerJSON.default_assignment_country_sys_id) {
                g_form.setValue("assignment_country", answerJSON.default_assignment_country_sys_id, answerJSON.default_assignment_country_display_value);
            } else {
                g_form.clearValue(("assignment_country"));
            }
        } else {
            g_form.clearValue(("assignment_country"));
            g_form.showFieldMsg("assignment_country", "Requester group has no country attached - please contact the Obsidian Team in order to be able to submit SKU assignment with that requester group", "error");
        }

    });


}
