catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (newValue == '') {
      g_form.clearValue("u_country");
      return;
   }

   var ga = new GlideAjax("x_nesa_ng_obsidian.DXCTechnicalTicketUtilsAJAX");
   ga.addParam("sysparm_name", "processRequesterGroup");
   ga.addParam("sysparm_group_sys_id", newValue);
   ga.getXML(function (response) {
      var answer = response.responseXML.documentElement.getAttribute("answer");
      var answerJSON = JSON.parse(answer);
      if (answerJSON.impacted_country_sys_id) {
         g_form.setValue("u_country", answerJSON.impacted_country_sys_id, answerJSON.impacted_country_display_value);
      } else {
         g_form.clearValue(("u_country"));
      }

   });

}
