catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) {
        return;
    }

    try {

		// STRY0190769 - The "SAP material number" field exhibits the SAP Material description and batch managed (Yes/No) of each selectable SAP material number, and these information remain on screen once a selection is made

        if (newValue != "") {
            var ga = new GlideAjax("x_nesa_ng_obsidian.DXCObsidianSKUUtilsAJAX");
            ga.addParam("sysparm_name", "getSAPMaterialNumberInfo");
            ga.addParam("sysparm_sap_material_number", newValue);
            ga.getXML(function (response) {
                var answer = response.responseXML.documentElement.getAttribute("answer");
                var answerObject = JSON.parse(answer);
                if (answerObject) {
					var displayValue = answerObject.number + " - " + answerObject.description + " (" + ( answerObject.batch_managed == "Yes" ? "batched managed" : "non-batched managed") + ")";
                    g_form.setValue("sap_material_number", newValue, displayValue);
                }
            });
        }

    } catch (e) {
        jslog("error in onChange Sap Material Number script: " + e);

    }
}
