sp_rectangle_menu_item.record_script:
// maximum number of entries in this Menu
var max = 30;

var t = data;  // shortcut
t.items = [];

var u = gs.getUser().getID();
// use record watchers to tell header when to update dropdown counts
t.record_watchers = [];
t.record_watchers.push({'table':'x_nesa_ng_obsidian_technical_ticket','filter':'opened_byDYNAMIC90d1921e5f510100a9ad2572f2b477fe^ORmarketDYNAMIC8ebd36ba1b46cd90fe9e43f1b24bcbd7^ORu_countryDYNAMIC81fd323e1b46cd90fe9e43f1b24bcbad'});

var z = new GlideRecord('x_nesa_ng_obsidian_technical_ticket');
z.addEncodedQuery('opened_byDYNAMIC90d1921e5f510100a9ad2572f2b477fe^ORmarketDYNAMIC8ebd36ba1b46cd90fe9e43f1b24bcbd7^ORu_countryDYNAMIC81fd323e1b46cd90fe9e43f1b24bcbad');
z.orderByDesc('sys_updated_on');
z.setLimit(max);
z.query();
while (z.next()) {
  if (!z.canRead())
    continue;

  var a = {};
  $sp.getRecordValues(a, z, 'title,sys_id,number,sys_updated_on,category');
  if (z.title.nil())
    a.short_description = "(No title)";
  else
    a.short_description = z.getValue('title');

  a.__page = 'standard_ticket';
  a.__table = z.getTableName();
  a.user = z.category.getDisplayValue();
  a.date = z.sys_updated_on.getDisplayValue();
  a.type = 'record';
  a.sortOrder = z.getValue('sys_updated_on');
  t.items.push(a);
}

t.items.sort(function(a, b) {
  return b.sortOrder - a.sortOrder;
});
t.items = t.items.slice(0,max);// only want first 30
t.count = t.items.length;
t.items=t.items.slice(0,5);

var link = {title: gs.getMessage(''), type: 'link', href: '#', items: []};
t.items.push(link); // put 'View all requests' first
