sys_relationship.query_with:
(function refineQuery(current, parent) {

    var modelSysIDs = [];

    var skuAssignment_GR = new GlideRecord('x_nesa_ng_obsidian_sku_assignment');
    skuAssignment_GR.addQuery('sku_stage', 600);
    skuAssignment_GR.addQuery('assignment_country', parent.country);
    skuAssignment_GR.orderBy('obsidian_model');
    skuAssignment_GR.orderByDesc('activation_date');
    skuAssignment_GR.query();

    var applicableModelSysIDs = [];

    while (skuAssignment_GR.next()) {
        if (modelSysIDs.indexOf((modelSysID = skuAssignment_GR.getValue("obsidian_model"))) == -1) {
            modelSysIDs.push(modelSysID);
            applicableModelSysIDs.push(skuAssignment_GR.getUniqueValue());
        }
    }

    current.addQuery('sys_id', applicableModelSysIDs);


})(current, parent);
