sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
   try {
      var modelFeature_GR = current.model_feature.getRefRecord();
      if (!modelFeature_GR.isValidRecord()) {
         throw ("couldn't get parent feature option GR");
      } else {
         var featureOption_GA = new GlideAggregate(x_nesa_ng_obsidian.DXCObsidianConstants.NML_TABLE_NAMES.FEATURE_OPTION_TABLE);
         featureOption_GA.addAggregate("COUNT");
         featureOption_GA.addQuery("model_feature", modelFeature_GR.getValue("sys_id"));
         featureOption_GA.addQuery("active", true);
         featureOption_GA.query();
         var activeFeatureOptionCount = 0;

         if (featureOption_GA.next()) {
            activeFeatureOptionCount = featureOption_GA.getAggregate("COUNT");
         }


         if (activeFeatureOptionCount == 1) {
            var featureOption_GR = new GlideRecord(x_nesa_ng_obsidian.DXCObsidianConstants.NML_TABLE_NAMES.FEATURE_OPTION_TABLE);
            featureOption_GR.addQuery("model_feature", modelFeature_GR.getValue("sys_id"));
            featureOption_GR.addQuery("active", true);
            featureOption_GR.query();
            featureOption_GR.next();
            modelFeature_GR.setValue("standard_option", featureOption_GR.getValue("sys_id"));
         }

         if (current.active == false && modelFeature_GR.getValue("standard_option") == current.sys_id ){
            modelFeature_GR.setValue("standard_option", "");
         }

         if (activeFeatureOptionCount === 0 && modelFeature_GR.getValue("standard_option") !== null) {
            modelFeature_GR.setValue("standard_option", "");
         }



         modelFeature_GR.setValue("active_option_count", activeFeatureOptionCount);
         modelFeature_GR.update();
      }
   } catch (exception) {
      gs.addErrorMessage("(DXC) update feature option count error: " + exception);
      var dxcLogger = new global.DXCGSLog("com.dxc.global.debugging_level", "(DXC) update feature option count");
      dxcLogger.logError(exception);
   }
})(current, previous);
