sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    try {
		g_form.setReadOnly('tsm.number', true);
		g_form.setReadOnly('tsm.state', true);
		g_form.setReadOnly('tsm.first_reported_model', true);
		g_form.setReadOnly('tsm.category', true);
		g_form.setReadOnly('tsm.defective_component', true);
		g_form.setReadOnly('tsm.origin', true);
		g_form.setReadOnly('tsm.master_ticket_id', true);
		g_form.setReadOnly('tsm.issue_name', true);
		g_form.setReadOnly('tsm.description', true);
    } catch (exception) {
        // Specify the name of the Client Script in the log message
        jslog('Error in Client Script <(DXC) Make referenced TSM fields read-only>: ' + exception);
    }
}
