sys_script_client.script:
function onLoad() {
	try {

		var stage = g_form.getValue('document_stage');
		if(stage == '10') // Draft
			g_form.removeOption('document_stage', '40');

		else if (stage == '30') // Published
			g_form.removeOption('document_stage', '99');

		else if(stage == '40') { // Obsolete {
			g_form.removeOption('document_stage', '99');
			g_form.removeOption('document_stage', '30');
		}

	} catch (exception) {
		// Specify the name of the Client Script in the log message
		jslog('Error in Client Script <(DXC) Mark stage required fields mandatory>: ' + exception);
	}
}
