sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
	/* Optional EmailOutbound */
	email, /* Optional GlideRecord */ email_action,
	/* Optional GlideRecord */
	event) {
		var lmaLogger = new global.GSLog("com.dxc.global.debugging_enabled", "LMADebug");
	try {


		var classLabel = current.getClassDisplayValue();

		if (current.getTableName() == "sn_uni_task_universal_task"){
			var parentGR = current.parent.getRefRecord();
			if (parentGR.isValidRecord()){
				classLabel = parentGR.getClassDisplayValue();
			}
		}

		template.print(classLabel);
	} catch (e) {
		lmaLogger.logErr("error in x_nesa_ng_obsidian.default_layout.header_text: " + e);
		template.print("error in x_nesa_ng_obsidian.default_layout.header_text: " + e);
	}

})(current, template, email, email_action, event);
