sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    try {
        var supplierID = current.getValue('supplier');
		var utils = new x_nesa_ng_obsidian.DXCObsidianCommonUtils();

		var recipients = utils.getEmailUsersRelatedWithSupplier(supplierID);
		if (!recipients)
			return;

		for (var i=0; i < recipients.length; i++)
			email.addAddress('cc', recipients[i]);

    } catch (e) {
        template.print('Error in "x_nesa_ng_obsidian.supplier_recipients" email script: ' + e);
    }

})(current, template, email, email_action, event);
