sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
   try {

      var cascadeNeeded = false;

      var currencyFieldNames_JSON = {
         "gross_price": null,
         "feature_price": null,
      };

      var featureOption_GR = new GlideRecord(x_nesa_ng_obsidian.DXCObsidianConstants.NML_TABLE_NAMES.FEATURE_OPTION_TABLE);
      featureOption_GR.addQuery("model_feature.obsidian_model", current.sys_id);
      featureOption_GR.query();
      while (featureOption_GR.next()) {
         for (var currencyFieldName in currencyFieldNames_JSON) {

            var currencyFieldValue = featureOption_GR.getValue(currencyFieldName);
            if (currencyFieldValue !== null) {
               var fxCurrencyInstance_GR = new GlideRecord("fx_currency2_instance");
               fxCurrencyInstance_GR.addQuery("parent_record", featureOption_GR.sys_id);
               fxCurrencyInstance_GR.addQuery("field", currencyFieldName);
               fxCurrencyInstance_GR.query();
               fxCurrencyInstance_GR.next();
               if (fxCurrencyInstance_GR.getValue("currency") !== current.default_currency) {
                  cascadeNeeded = true;
                  fxCurrencyInstance_GR.setValue("currency", current.default_currency);
                  fxCurrencyInstance_GR.update();
               }
            }
         }
      }

      if (cascadeNeeded) {
         gs.addInfoMessage(gs.getMessage("Currency has been updated in underlying Feature Option records without conversion - please verify actual values to ensure correctness"));
      }
   } catch (exception) {
      gs.addErrorMessage("Error in '(DXC) Cascade Default Currency change' business rule: " + exception);
      var dxcLogger = new global.DXCGSLog("com.dxc.global.debugging_level", "(DXC) Cascade Default Currency change");
      dxcLogger.logError(exception);
   }
})(current, previous);
