sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
	try {

		var transit_gr = new GlideRecord('x_nesa_ng_obsidian_transit_lead_time');
		transit_gr.addQuery('importing_country', current.importing_country);
		transit_gr.addQuery('departing_country', current.departing_country);
		transit_gr.setLimit(1);
		transit_gr.query();
		if(transit_gr.hasNext()) {
			var msg = 'A "Transit lead time" record already exists for transits from ' + current.getDisplayValue('departing_country') + ' to ' + current.getDisplayValue('importing_country');
			gs.addErrorMessage(msg);
			current.setAbortAction(true);
		}

	} catch (exception) {
		var dxcLogger = new global.DXCGSLog("com.dxc.global.debugging_enabled",
											"(DXC)prevent transit lead time duplicate");
		dxcLogger.logWarning(exception);
	}
})(current, previous);
