sys_script_include.script:
var DXCObsidianPortalUtils = Class.create();
DXCObsidianPortalUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	/**
     * @author	DXC - Hien Tran
     * @desc Retrieve the number of records for the specific tab of the table on the obsidian_index page
     * @param 'table_name': name of the current table, 'query_statement': current filter
     * @return number of records for the current query
     */
	count: function() {
		var count = 0;
		var table = this.getParameter("table_name");
		var query = this.getParameter("query_statement");
		var gr = new GlideRecordSecure(table);
		gr.addEncodedQuery(query);
		gr.query();
		while (gr.next()) {
			count += 1;
		}
		return count.toString();
	},

	/**
     * @author	DXC - Hien Tran
     * @desc Retrieve the number of records for all the tabs of the table on the obsidian_index page when loading the page
     * @param 'list_filter': list of the filter for each tab
     * @return number of records for each tab
     */
	countMultiple: function() {
		var listFilter = this.getParameter("list_filter");
		var arr = JSON.parse(listFilter);
		var listCount = [];
		for (var i = 0; i < arr.length; i++) {
			var gr = new GlideRecordSecure(arr[i].table);
			gr.addEncodedQuery(arr[i].filter);
			gr.query();
			var count = 0;
			while (gr.next()) {
				count +=1;
			}
			listCount.push(count.toString());
		}
		return JSON.stringify(listCount);
	},
	type: 'DXCObsidianPortalUtils'
});
