sys_script_include.script:
var DXCTechnicalTicketUtilsAJAX = Class.create();
DXCTechnicalTicketUtilsAJAX.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {



    processRequesterGroup: function () {
        var lmaLogger = new global.GSLog("com.dxc.global.debugging_enabled", "LMADebug");
        var groupSysId = this.getParameter("sysparm_group_sys_id");

        var answerJSON = {
            impacted_country_sys_id: null,
            impacted_country_display_value: null,
        };


        var coveredLocation_GR = new GlideRecord('sys_group_covers_location');
        coveredLocation_GR.addQuery("group", groupSysId);
        coveredLocation_GR.addQuery("location.u_type", "country");
        coveredLocation_GR.query();
        if (coveredLocation_GR.next()) {
            answerJSON.impacted_country_sys_id = coveredLocation_GR.getValue("location");
            answerJSON.impacted_country_display_value = coveredLocation_GR.getDisplayValue("location");
        }


        return JSON.stringify(answerJSON, null, 4, 4);


    },

    getObsidianModelInformation: function () {
        var modelId = this.getParameter('sysparm_model_id');

        var result = this.newItem("result");
        result.setAttribute("supplier", "");
        result.setAttribute("model", "");
        result.setAttribute("version", "");

        var modelGR = new GlideRecord(x_nesa_ng_obsidian.DXCObsidianConstants.NML_TABLE_NAMES.MODEL_TABLE);
        if (!modelGR.get(modelId)) {
            return;
        }

        result.setAttribute("supplier", modelGR.getValue('supplier'));
        result.setAttribute("model", modelGR.getUniqueValue());
        result.setAttribute("version", modelGR.version);
    },




    processExcelFileInToVariableSet: function () {
        var attachmentID = this.getParameter('sysparm_attachment_id');
        var attachmentGR = new GlideRecord('sys_attachment');
        if (!attachmentGR.get(attachmentID))
            return "";

        var utils = new DXCTechnicalTicketUtils();
        var serialsArray = utils.processExcelFileWithImpactedSerials(attachmentID);

        return JSON.stringify(serialsArray);
    },


