sys_transform_entry.source_script:
answer = (function transformEntry(source) {

	var modelFeatureFullName = source.u_model_feature;

	if (modelFeatureFullName == ""){
		return "modelFeatureFullName_is_empty";
	}

	var modelFeatureFullNameArray = modelFeatureFullName.split("---");

	if (modelFeatureFullNameArray.length != 2){
		return "modelFeatureFullNameArray.length_is_not_2";
	}

	var modelName = modelFeatureFullNameArray[0].toString();
	var featureName = modelFeatureFullNameArray[1].toString();

	var modelFeature_GR = new GlideRecord(x_nesa_ng_obsidian.DXCObsidianConstants.NML_TABLE_NAMES.MODEL_FEATURE_TABLE);
	modelFeature_GR.addQuery("obsidian_model.name", modelName);
	modelFeature_GR.addQuery("name", featureName);
	modelFeature_GR.query();
	if (modelFeature_GR.getRowCount() !=1){
		return modelFeature_GR.getRowCount() + "_model_feature_found";
	}
	modelFeature_GR.next();
	return modelFeature_GR.sys_id;

})(source);
