sys_ui_action.script:
function openNewSKUglideModal() {

    if (g_form.getValue("obsidian_sku") != "") {

        var glideModal = new GlideModal("glide_alert_standard");
        glideModal.setTitle("Cannot create new SKU Code");
        glideModal.setPreference('warning', true);
        glideModal.setPreference('title', 'The Obsidian SKU field is already filled in.\nPlease empty Obsidian SKU reference before creating a new SKU for this assignment.');
        glideModal.render();

    } else {
        var glideModalForm = new GlideModalForm('Create new SKU Code', 'x_nesa_ng_obsidian_obsidian_sku');
        glideModalForm.setPreference('sysparm_view', '');

        var sysparmQueryString = 'obsidian_model=' + g_form.getValue("obsidian_model");
        sysparmQueryString += "^model_configuration=" + g_form.getValue("model_configuration");
        sysparmQueryString += "^source_sku_assignment=" + g_form.getUniqueValue();
        sysparmQueryString += "^sku_vendor=" + g_form.getValue("sku_vendor");

        glideModalForm.addParm('sysparm_query', sysparmQueryString);
        glideModalForm.render();
        glideModalForm.setCompletionCallback(function (action_verb, sys_id, table, displayValue) {
            if (sys_id) {
                g_form.setValue("obsidian_sku", sys_id, displayValue);
                gsftSubmit(null, g_form.getFormElement(), 'sysverb_create_new_sku_code');
            }
        });
    }

}



if (typeof window == 'undefined') {
    serverSideCode();
}

function serverSideCode() {
    action.setRedirectURL(current);
    current.update();
}
