sys_ui_page.client_script:
function invokeCallback(choice) {
	var callbackFunctionName = "";
	var gdwCallback = GlideDialogWindow.get();
	if (choice == "yes"){
		var comments = gel("feedback-value").value;
		var ga = new GlideAjax('x_nesa_ng_obsidian.DXCObsidianDocumentUtilsAJAX');
		ga.addParam('sysparm_name', 'startSupplierFeedbackFlow');
		ga.addParam('sysparm_feedback', comments);
		ga.addParam('sysparm_library', g_form.getUniqueValue());
		ga.getXMLAnswer(function(answer) {

		});
		callbackFunctionName = "onYes";
	}else{
		callbackFunctionName = "onNo";
	}

	var f = gdwCallback.getPreference(callbackFunctionName);
	if (typeof(f) == "function") {
		try {
			f.call(gdwCallback, gdwCallback.getPreference("oldValue"));
		} catch(e) {
		}
	}
	gdwCallback.destroy();
	return false;
}
var button = document.getElementById("yes_button");
button.disabled = true;

var input = document.getElementById("feedback-value");
input.value = '';

var gdwCustom = GlideDialogWindow.get();
var focusButton = gdwCustom.getPreference("defaultButton");
if (focusButton == null){
	focusButton = "yes_button";
}else if(focusButton == "yes_button"){
	gel("no_button").classList.add("btn-default");
}else{
	gel("yes_button").classList.add("btn-default");
}
gel(focusButton).classList.add("btn-primary");
gel(focusButton).focus();

var popupIcon = gdwCustom.getPreference("popupIcon");
if (popupIcon == null){
	gel("popup_icon").classList.add("icon-check-circle");
}else{
	gel("popup_icon").classList.add(popupIcon);
}
var popupIconColor = gdwCustom.getPreference("popupIconColor");
if (popupIconColor != null){
	gel("popup_icon").style.color = popupIconColor;
}

function success() {

	if(input.value.length == 0) {
		document.getElementById('yes_button').disabled = true; 
	} else { 
		document.getElementById('yes_button').disabled = false;
	}
}

