sys_ui_script.script:
var x_nesa_ng_obsidian = x_nesa_ng_obsidian || {};

x_nesa_ng_obsidian.DXCObsidianUIscript = (function() {
    "use strict";

    /* set your private variables and functions here. For example: 
    	var privateVar = 0; 
    	function private_function() {
    		return ++privateVar;
    	}
    */

    /* Share variables between multiple UI scripts by adding them to your scope object. For example: 
    	x_nesa_ng_obsidian.sharedVar = 0; 

     Then access them in your scripts the same way. For example: 
    	function get_shared() {
    		return x_nesa_ng_obsidian.sharedVar;
    	}
    */

    return {

        processCurrencyFieldChange: function(currencyFieldName, newValue) {
            var newValueCurrency = newValue.substring(0, 3, 0);
            if (newValueCurrency !== g_scratchpad.default_currency) {
                g_form.setValue(currencyFieldName, newValue.replace(newValueCurrency, g_scratchpad.default_currency));
                g_form.showFieldMsg(currencyFieldName, getMessage("Only the default currency can be used:") + " " + g_scratchpad.default_currency, "error");
            } else {
                g_form.hideFieldMsg(currencyFieldName, true);
            }
        },

        type: "DXCObsidianUIscript"
    };


})();
